     1→/**
     2→ * QueryInterface Component
     3→ *
     4→ * Natural language query interface for governance analytics.
     5→ * Features:
     6→ * - Text input for natural language questions
     7→ * - Sample query suggestions
     8→ * - Query result display with answer and data
     9→ * - Loading, error, and success states
    10→ * - Query history (recent queries)
    11→ */
    12→
    13→import { useCallback, useState, useRef, useEffect } from "react";
    14→import {
    15→  AlertCircle,
    16→  ChevronDown,
    17→  ChevronUp,
    18→  Clock,
    19→  MessageSquare,
    20→  Search,
    21→  Send,
    22→  Sparkles,
    23→  X,
    24→} from "lucide-react";
    25→
    26→/** Query response data structure from the API */
    27→interface QueryResponse {
    28→  query: string;
    29→  answer: string;
    30→  data: Record<string, unknown>;
    31→  query_understood: boolean;
    32→  generated_at: string;
    33→}
    34→
    35→/** Query history item */
    36→interface QueryHistoryItem {
    37→  query: string;
    38→  response: QueryResponse;
    39→  timestamp: Date;
    40→}
    41→
    42→/** Widget loading state */
    43→type LoadingState = "idle" | "loading" | "success" | "error";
    44→
    45→/** API URL from environment */
    46→const API_BASE_URL =
    47→  import.meta.env.VITE_ANALYTICS_API_URL || "http://localhost:8080";
    48→
    49→/** Maximum number of queries to keep in history */
    50→const MAX_HISTORY_ITEMS = 5;
    51→
    52→/** Sample queries to help users get started */
    53→const SAMPLE_QUERIES = [
    54→  "Show violations this week",
    55→  "Which policy is violated most?",
    56→  "What is the compliance trend?",
    57→  "How many violations occurred yesterday?",
    58→  "List critical security incidents",
    59→];
    60→
    61→/**
    62→ * Formats a timestamp for display
    63→ */
    64→function formatTimestamp(date: Date): string {
    65→  return date.toLocaleString(undefined, {
    66→    dateStyle: "short",
    67→    timeStyle: "short",
    68→  });
    69→}
    70→
    71→/**
    72→ * Formats data for display in a readable way
    73→ */
    74→function formatDataValue(value: unknown): string {
    75→  if (value === null || value === undefined) {
    76→    return "N/A";
    77→  }
    78→  if (typeof value === "object") {
    79→    return JSON.stringify(value, null, 2);
    80→  }
    81→  return String(value);
    82→}
