    96→  const [loadingState, setLoadingState] = useState<LoadingState>("idle");
    97→  const [error, setError] = useState<string | null>(null);
    98→  const [currentResponse, setCurrentResponse] = useState<QueryResponse | null>(
    99→    null
   100→  );
   101→  const [queryHistory, setQueryHistory] = useState<QueryHistoryItem[]>([]);
   102→  const [showHistory, setShowHistory] = useState(false);
   103→  const [showSamples, setShowSamples] = useState(true);
   104→  const inputRef = useRef<HTMLInputElement>(null);
   105→
   106→  /**
