   112→    }
   113→  }, []);
   114→
   115→  /**
   116→   * Submits a query to the API
   117→   */
   118→  const submitQuery = useCallback(async (queryText: string) => {
   119→    if (!queryText.trim()) {
   120→      return;
   121→    }
   122→
   123→    setLoadingState("loading");
   124→    setError(null);
   125→    setShowSamples(false);
   126→
   127→    try {
   128→      const response = await fetch(`${API_BASE_URL}/query`, {
   129→        method: "POST",
   130→        headers: {
   131→          "Content-Type": "application/json",
   132→          Accept: "application/json",
   133→        },
   134→        body: JSON.stringify({ question: queryText }),
   135→      });
   136→
   137→      if (!response.ok) {
   138→        const errorData = await response.json().catch(() => ({}));
   139→        throw new Error(
   140→          errorData.detail || `Query failed: ${response.status}`
   141→        );
   142→      }
   143→
   144→      const data: QueryResponse = await response.json();
   145→      setCurrentResponse(data);
   146→      setLoadingState("success");
   147→
   148→      // Add to history
   149→      setQueryHistory((prev) => {
   150→        const newItem: QueryHistoryItem = {
   151→          query: queryText,
   152→          response: data,
   153→          timestamp: new Date(),
   154→        };
   155→        const updated = [newItem, ...prev.slice(0, MAX_HISTORY_ITEMS - 1)];
   156→        return updated;
   157→      });
   158→    } catch (err) {
   159→      const message =
   160→        err instanceof Error ? err.message : "Failed to process query";
   161→      setError(message);
   162→      setLoadingState("error");
   163→    }
   164→  }, []);
   165→
   166→  /**
