   182→  /**
   183→   * Handle history item click
   184→   */
   185→  const handleHistoryClick = (item: QueryHistoryItem) => {
   186→    setQuery(item.query);
   187→    setCurrentResponse(item.response);
   188→    setLoadingState("success");
   189→    setShowHistory(false);
   190→  };
   191→
   192→  /**
   193→   * Clear current response and reset
   194→   */
   195→  const handleClear = () => {
   196→    setQuery("");
   197→    setCurrentResponse(null);
   198→    setLoadingState("idle");
   199→    setError(null);
   200→    setShowSamples(true);
   201→    inputRef.current?.focus();
   202→  };
   203→
   204→  return (
   205→    <div className="rounded-lg bg-white p-6 shadow">
   206→      {/* Header */}
   207→      <div className="mb-4 flex items-center gap-2">
   208→        <MessageSquare className="h-5 w-5 text-indigo-600" />
   209→        <h2 className="text-lg font-semibold text-gray-900">
   210→          Ask About Governance
   211→        </h2>
   212→        <Sparkles className="h-4 w-4 text-yellow-500" />
   213→      </div>
   214→
   215→      {/* Query Input Form */}
   216→      <form onSubmit={handleSubmit} className="relative">
   217→        <div className="flex gap-2">
   218→          <div className="relative flex-1">
   219→            <input
   220→              ref={inputRef}
   221→              type="text"
   222→              value={query}
   223→              onChange={(e) => setQuery(e.target.value)}
   224→              placeholder="Ask a question about governance data..."
   225→              className="w-full rounded-lg border border-gray-300 py-3 pl-10 pr-4 text-sm transition-colors focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500"
   226→              disabled={loadingState === "loading"}
   227→              maxLength={500}
   228→              aria-label="Query input"
   229→            />
   230→            <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-gray-400" />
   231→            {query && loadingState !== "loading" && (
   232→              <button
   233→                type="button"
   234→                onClick={handleClear}
   235→                className="absolute right-3 top-1/2 -translate-y-1/2 rounded-full p-1 text-gray-400 hover:bg-gray-100 hover:text-gray-600"
   236→                title="Clear"
   237→                aria-label="Clear query"
   238→              >
   239→                <X className="h-4 w-4" />
   240→              </button>
   241→            )}
   242→          </div>
   243→          <button
   244→            type="submit"
   245→            disabled={!query.trim() || loadingState === "loading"}
   246→            className="flex items-center gap-2 rounded-lg bg-indigo-600 px-4 py-3 text-sm font-medium text-white transition-colors hover:bg-indigo-700 disabled:cursor-not-allowed disabled:opacity-50"
   247→            aria-label="Submit query"
   248→          >
   249→            {loadingState === "loading" ? (
   250→              <div className="h-4 w-4 animate-spin rounded-full border-2 border-white border-t-transparent" />
